# views.py

from django.shortcuts import render
from .models import DoctorAppointment, PatientAppointment, MedicalAidAppointment
def doctor_appointment_list(request):
    doctors = Doctor.objects.all()
    return render(request, 'doctor_appointment/doctor_appointment_list.html', {'doctors': doctors})

def doctor_appointment_detail(request, doctor_id):

    doctor = Doctor.objects.get(id=doctor_id)

    appointments = doctor.appointments.all()

    return render(request, 'doctor_appointment/doctor_appointment_detail.html', {'doctor': doctor, 'appointments': appointments})

def patient_appointment_list(request, patient_id):

    patient = Patient.objects.get(id=patient_id)

    appointments = patient.appointments.all()

    return render(request, 'patient_appointment/patient_appointment_list.html', {'patient': patient, 'appointments': appointments})

def patient_appointment_detail(request, patient_id, appointment_id):

    appointment = PatientAppointment.objects.get(id=appointment_id)

    return render(request, 'patient_appointment/patient_appointment_detail.html', {'appointment': appointment})

def medical_aid_appointment_list(request, medical_aid_id):

    medical_aid = MedicalAid.objects.get(id=medical_aid_id)

    appointments = medical_aid.appointments.all()

    return render(request, 'medical_aid/medical_aid_appointment_list.html', {'medical_aid': medical_aid, 'appointments': appointments})

def medical_aid_appointment_detail(request, medical_aid_id, appointment_id):

    appointment = MedicalAidAppointment.objects.get(id=appointment_id)

    return render(request, 'medical_aid/medical_aid_appointment_detail.html', {'appointment': appointment})

