from django.db import models

class Doctor(models.Model):

    name = models.CharField(max_length=255)

    def __str__(self):

        return self.name

class Patient(models.Model):

    name = models.CharField(max_length=255)

    def __str__(self):

        return self.name

class MedicalAid(models.Model):

    name = models.CharField(max_length=255)

    def __str__(self):

        return self.name

class DoctorAppointment(models.Model):

    doctor = models.ForeignKey('Doctor', on_delete=models.CASCADE, related_name='appointments')

    patient = models.ForeignKey('Patient', on_delete=models.CASCADE, related_name='appointments')

    date = models.DateField()

    def __str__(self):

        return f'{self.patient.name} - {self.doctor.name} - {self.date}'

class PatientAppointment(models.Model):

    patient = models.ForeignKey('Patient', on_delete=models.CASCADE, related_name='appointments')

    doctor = models.ForeignKey('Doctor', on_delete=models.CASCADE, related_name='appointments')

    date = models.DateField()

    def __str__(self):

        return f'{self.patient.name} - {self.doctor.name} - {self.date}'

class MedicalAidAppointment(models.Model):

    medical_aid = models.ForeignKey('MedicalAid', on_delete=models.CASCADE, related_name='appointments')

    patient = models.ForeignKey('Patient', on_delete=models.CASCADE, related_name='appointments')

    date = models.DateField()

    def __str__(self):

        return f'{self.patient.name} - {self.medical_aid.name} - {self.date}'

