function calculateAreaAndPerimeter<T extends Shape>(shape: T): { area: number; perimeter: number } {
  const area = shape.area();
  const perimeter = shape.perimeter();
  return { area, perimeter };
}

type Shape = {
  area(): number;
  perimeter(): number;
}

class Circle implements Shape {
  constructor(private radius: number) {}

  area(): number {
    return Math.PI * Math.pow(this.radius, 2);
  }

  perimeter(): number {
    return 2 * Math.PI * this.radius;
  }
}

class Rectangle implements Shape {
  constructor(private width: number, private height: number) {}

  area(): number {
    return this.width * this.height;
  }

  perimeter(): number {
    return 2 * (this.width + this.height);
  }
}

const circle = new Circle(5);
const rectangle = new Rectangle(4, 6);

console.log(calculateAreaAndPerimeter(circle)); // { area: 78.53981633974483, perimeter: 31.41592653589793 }
console.log(calculateAreaAndPerimeter(rectangle)); // { area: 24, perimeter: 20 }
