        }
        m_pointers.clear();

        // Obnovení m_wait_state
        m_wait_state = false;
    }

    // Přidání ukazatele do vektoru
    void addPointer(void* ptr) {
        m_pointers.push_back(ptr);
    }

private:
    // Soukromý konstruktor pro Singleton
    GpuOffloadContext() : m_wait_state(false) {}

    // Zamezit kopírování
    GpuOffloadContext(const GpuOffloadContext&) = delete;
    GpuOffloadContext& operator=(const GpuOffloadContext&) = delete;

    // Členské proměnné
    bool m_wait_state;
    std::vector<void*> m_pointers;
};
